mapper.update:
update orders
cancel_reason=#{cancelReason}, rejection_reason=#{rejectionReason}, cancel_time=#{cancelTime}, pay_status=#{payStatus}, pay_method=#{payMethod}, checkout_time=#{checkoutTime}, status = #{status}, delivery_time = #{deliveryTime}
where id = #{id}

mapper.select:
select sum(amount) from orders


        and order_time > #{min}


        and order_time < #{max}


        and status = #{status}

select count(*) from orders


        and order_time > #{min}


        and order_time < #{max}


        and status = #{status}
